    public void Draw(Game game) {
        if (TipMapa == 1) {
            //Graphics gr=Shortcut.g;
            //Graphics gr1=Shortcut.g;
            for (int i = 0; i < mapa.length; i++)
                for (int j = 0; j < mapa[i].length; j++)
                    if (mapa[i][j] != 0) {
                        if (mapa[i][j] != 0 && mapa[i][j] != 11 && mapa[i][j] != 18 && mapa[i][j] != 19 && mapa[i][j]!=35) {
                            Tile.GetTile(mapa[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            Rectangle1.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                           // Shortcut.g.drawRect(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT);
                        } else if (mapa[i][j] == 11) {
                            Tile.GetTile(mapa[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleAur.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectangleAur.lastElement())==true)
                            {
                                mapa[i][j]=0;
                                Shortcut.game.Coins++;
                            }
                        }
                        else if(mapa[i][j]==18)
                        {
                            Tile.GetTile(mapa[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleDiamant.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectangleDiamant.lastElement())==true)
                            {
                                mapa[i][j]=0;
                                Shortcut.game.Diamonds++;
                            }
                        }
                        else if(mapa[i][j]==19)
                        {
                            Tile.GetTile(mapa[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectanglePocal.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectanglePocal.lastElement())==true)
                            {
                                mapa[i][j]=0;
                                Shortcut.game.Goblets++;
                            }
                        }
                        else if(mapa[i][j]==20)
                        {
                            Tile.GetTile(mapa[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleWin.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectangleWin.lastElement())==true)
                            {
                                mapa[i][j]=0;
                                Shortcut.game.Crown++;
                            }
                        }
                        else if(mapa[i][j]==35)
                        {
                            Tile.GetTile(mapa[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleWin.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectangleWin.lastElement())==true)
                            {
                                JackClass.Reset();
                                State.SetState(Shortcut.game.win);
                            }
                        }

                    }
        } else if (TipMapa == 2) {
            for (int i = 0; i < mapaL2.length; i++)
                for (int j = 0; j < mapaL2[i].length; j++)
                    if (mapaL2[i][j] != 0 ) {
                        if (mapaL2[i][j] != 0 && mapaL2[i][j]!=11 && mapaL2[i][j] != 18 && mapaL2[i][j] != 19 && mapaL2[i][j]!=35) {
                            Tile.GetTile(mapaL2[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            Rectangle1.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                        }
                        else if (mapaL2[i][j] == 11) //gasesc o moneda
                        {
                            Tile.GetTile(mapaL2[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleAur.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));

                            if(GetGold(RectangleAur.lastElement())==true)
                            {
                                mapaL2[i][j]=0;
                                Shortcut.game.Coins++;
                            }
                        }
                        else if(mapaL2[i][j]==18) //diamant
                        {
                            Tile.GetTile(mapaL2[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleDiamant.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectangleDiamant.lastElement())==true)
                            {
                                mapaL2[i][j]=0;
                                Shortcut.game.Diamonds++;

                            }
                        }
                        else if(mapaL2[i][j]==19) //pocal
                        {
                            Tile.GetTile(mapaL2[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectanglePocal.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectanglePocal.lastElement())==true)
                            {
                                mapaL2[i][j]=0;
                                Shortcut.game.Goblets++;

                            }
                        }
                        else if(mapaL2[i][j]==35) //coroana
                        {
                            Tile.GetTile(mapaL2[i][j]).Draw(Shortcut.g, j * 48, i * 48);
                            RectangleWin.add(new Rectangle(j * Tile.TILE_HEIGHT, i * Tile.TILE_WIDTH, Tile.TILE_WIDTH, Tile.TILE_HEIGHT));
                            if(GetGold(RectangleWin.lastElement())==true)
                            {
                                State.SetState(Shortcut.game.Final);
                            }
                        }

                    }
        }
    }
    public boolean GetGold(Rectangle r) {
            if (r.intersects(JackClass.cutie))
            {
                return true;
            }

        return false;
    }

